Map:
  &fontset-settings;


		[place] = 'city'
      		&maxscale_zoom1;
      		&minscale_zoom19;


		[place] = 'town'
      		&maxscale_zoom1;
      		&minscale_zoom19;


		[place] = 'village'
      		&maxscale_zoom1;
      		&minscale_zoom19;




    feltcode


      (select way,"name",place,"ref:se:scb" from &prefix;_point where "place" in ('city','town','village') AND ("ref:se:scb" not like '____') AND ("ref:se:scb" is not null) order by place) as places	

      &datasource-settings;
